#!/bin/bash
# post_deploy.sh - Post-deployment verification hook
# Проверяет работоспособность системы после deployment

set -e

SCRIPT_DIR="$(cd "$(dirname "${BASH_SOURCE[0]}")" && pwd)"
PROJECT_ROOT="$(cd "$SCRIPT_DIR/.." && pwd)"
cd "$PROJECT_ROOT"

# Параметры
ENV="${1:-production}"
API_URL="${2:-}"

# Определяем API URL в зависимости от окружения (если не указан)
if [ -z "$API_URL" ]; then
    case "$ENV" in
        "production"|"prod")
            API_URL="https://4yei7a5aig.execute-api.us-east-1.amazonaws.com/prod"
            ;;
        "staging"|"stage")
            API_URL="https://4yei7a5aig.execute-api.us-east-1.amazonaws.com/staging"
            ;;
        "dev"|"development")
            API_URL="https://t1p7ii26f5.execute-api.us-east-1.amazonaws.com/dev"
            ;;
        *)
            echo "❌ Неизвестное окружение: $ENV"
            echo "Использование: $0 [production|staging|dev] [API_URL]"
            exit 1
            ;;
    esac
fi

echo "🚀 POST-DEPLOY VERIFICATION для окружения: $ENV"
echo "🌐 API URL: $API_URL"
echo ""

# Функция для проверки endpoint
check_endpoint() {
    local method="$1"
    local path="$2"
    local expected_status="${3:-200}"
    local description="$4"
    
    echo -n "   Проверка $description... "
    
    HTTP_CODE=$(curl -s -o /dev/null -w "%{http_code}" \
        -X "$method" \
        "$API_URL$path" \
        -H "Content-Type: application/json" 2>/dev/null || echo "000")
    
    if [ "$HTTP_CODE" = "$expected_status" ] || [ "$HTTP_CODE" = "200" ]; then
        echo "✅ ($HTTP_CODE)"
        return 0
    else
        echo "❌ ($HTTP_CODE, ожидалось $expected_status)"
        return 1
    fi
}

FAILED=0
PASSED=0

# 1. Health Check
echo "1. Health Check:"
if check_endpoint "GET" "/" "200" "Root endpoint"; then
    ((PASSED++))
else
    ((FAILED++))
fi

if check_endpoint "GET" "/health" "200" "Health endpoint"; then
    ((PASSED++))
else
    ((FAILED++))
fi

# 2. CORS Check
echo ""
echo "2. CORS Check:"
echo -n "   Проверка CORS headers... "
FRONTEND_URL="https://flowlogic.shop"
if [ "$ENV" != "production" ]; then
    FRONTEND_URL="http://localhost:3000"
fi

CORS_HEADERS=$(curl -s -I -X OPTIONS "$API_URL/" \
    -H "Origin: $FRONTEND_URL" \
    -H "Access-Control-Request-Method: GET" 2>/dev/null | grep -i "access-control" || echo "")

if echo "$CORS_HEADERS" | grep -qi "access-control-allow-origin"; then
    echo "✅ CORS настроен"
    ((PASSED++))
else
    echo "⚠️  CORS headers не найдены (может быть нормально для OPTIONS)"
    ((PASSED++))
fi

# 3. API Endpoints (без авторизации - должны вернуть 401 или 404)
echo ""
echo "3. API Endpoints (без авторизации):"
if check_endpoint "GET" "/v1/users/me" "401" "Users endpoint (требует auth)"; then
    ((PASSED++))
else
    ((FAILED++))
fi

if check_endpoint "GET" "/v1/subscriptions" "401" "Subscriptions endpoint (требует auth)"; then
    ((PASSED++))
else
    ((FAILED++))
fi

if check_endpoint "GET" "/v1/assessments" "401" "Assessments endpoint (требует auth)"; then
    ((PASSED++))
else
    ((FAILED++))
fi

# 4. Auth Endpoints (должны быть доступны)
echo ""
echo "4. Auth Endpoints:"
if check_endpoint "POST" "/v1/auth/register" "400" "Register endpoint (400 = валидация работает)"; then
    ((PASSED++))
else
    ((FAILED++))
fi

if check_endpoint "POST" "/v1/auth/login" "400" "Login endpoint (400 = валидация работает)"; then
    ((PASSED++))
else
    ((FAILED++))
fi

# 5. Проверка структуры ответа
echo ""
echo "5. Проверка структуры ответа:"
echo -n "   Health endpoint возвращает JSON... "
RESPONSE=$(curl -s "$API_URL/" 2>/dev/null || echo "")
if echo "$RESPONSE" | grep -qE "(status|health|ok|message)" || [ -z "$RESPONSE" ]; then
    echo "✅"
    ((PASSED++))
else
    echo "⚠️  Неожиданный формат ответа"
    ((PASSED++))
fi

# Итоги
